/** @internal */
export type ControllerRight = {
    actions: {
        [action: string]: boolean;
    };
};
/**
 * @internal
 * A Controller Rights definition
 *
 * @example
 * {
 *   "document": {
 *     actions: {
 *       "create": true,
 *     }
 *   }
 * }
 */
export type ControllerRights = {
    [controller: string]: ControllerRight;
};
